Console.WriteLine($"Part One = {Enumerable.Range(0, 3).SelectMany(o => File.ReadAllLines("input.txt").First().Skip(o).Chunk(4).Select((s, i) => new { Index = (i * 4) + o, Marker = s })).Where(m => m.Marker.Distinct().Count() == m.Marker.Length).OrderBy(m => m.Index).First().Index + 4}");
Console.WriteLine($"Part Two = {Enumerable.Range(0, 13).SelectMany(o => File.ReadAllLines("input.txt").First().Skip(o).Chunk(14).Select((s, i) => new { Index = (i * 14) + o, Marker = s })).Where(m => m.Marker.Distinct().Count() == m.Marker.Length).OrderBy(m => m.Index).First().Index + 14}");
